        {
            boy_count++;

            str[i] = '.';
            str[i + 1] = '.';
            str[i + 2] = '.';
        }
        else if ((str[i] == 'b' && str[i + 1] == 'o') 
            || (str[i] == 'o' && str[i + 1] == 'y'))
        {
            boy_count++;

            str[i] = '.';
            str[i + 1] = '.';
        }
        else if (str[i] == 'b' 
            || str[i] == 'o' 
            || str[i] == 'y')
        {
            boy_count++;

            str[i] = '.';
        }
        else if (str[i] == 'g' && str[i + 1] == 'i' && str[i + 2] == 'r' && str[i + 3] == 'l')
        {
            girl_count++;

            str[i] = '.';
            str[i + 1] = '.';
            str[i + 2] = '.';
            str[i + 3] = '.';
        }
        else if ((str[i] == 'g' && str[i + 1] == 'i' && str[i + 2] == 'r') ||
            (str[i] == 'i' && str[i + 1] == 'r' && str[i + 2] == 'l'))
        {
            girl_count++;

            str[i] = '.';
            str[i + 1] = '.';
            str[i + 2] = '.';
        }
        else if ((str[i] == 'g' && str[i + 1] == 'i') ||
            (str[i] == 'r' && str[i + 1] == 'l') ||
            (str[i] == 'i' && str[i + 1] == 'r'))
        {
            girl_count++;

            str[i] = '.';
            str[i + 1] = '.';
        }
        else if (str[i] == 'g'
            || str[i] == 'i'
            || str[i] == 'r'
            || str[i] == 'l')
        {
            girl_count++;

            str[i] = '.';
        }
    }

    printf("%d\n%d\n", boy_count, girl_count);

    return 0;
