{
	int an=1;
	for(int p=1;p<=b;p++) an*=10;
	return an;
}
void cs(int k,int l)
{
	if(k/pow(l)) cs(k,l+1);//递归拆数高位在前
	int k1;
	k1=((k%pow(l))/pow(l-1));//从右往左第l位,如543%pow(3)/pow(3-1)=543%1000/100=5(从右往左第三位)	
	a[++num]=k1;//存入a数组
	next[num]=num+1;//表示下一个数的位置
	pre[num]=num-1;//表示上一个数的位置
}
k[1]=1;//1是集合元素
for(int p=1;p<=5000000;p++)
{
