    int x,y; //岛屿坐标 临时变量
    for(int i=1;i<=N;i++)
    { 
       cin>>x>>y;
       double t=sqrt(R*R-y*y);
       P[i].l=x-t;
       P[i].r=x+t;
    } 
    sort(P+1,P+1+N,cmp);//把cmp函数传值
    double lst; 
    int now=1;
    int ans=0;
    while(1)
    {
        lst=P[now].r;
        ans++;
        while(P[now].l<lst)
        {
            now++;
            if(now>N)
            {
                cout<<ans<<endl; //输出
                return 0;
            }
        }
    }
    return 0;
}
