#include<bits/stdc++.h>
#define int long long
using namespace std;
int s,t,n;
signed main(){
//	freopen("bx.in","r",stdin);freopen("bx.out","w",stdout);
	while(scanf("%lld%lld%lld",&s,&t,&n)!=EOF){
		int ans=0;
		if (s<n){
			ans=3*s+n-s-1;
			if (!t) ans++;
		}
		else{
			ans=3*max(n-1,min(n,s-t));
			if (s-(n-1)==t) ans++;
		}
		printf("%lld ",ans);
		ans=3+n-1-min(t,n-1);
		if (s<=t) ans=min(ans,max(0ll,n-t+s));
		printf("%lld\n",ans);
	}
	return 0;
}
