                        perfect = 3 * n;//这里的perfect也是按照perfect递减来枚举的。  
                    else  
                        if (s - t == n - 1) //如果能n-1场都至少赢一球 ,另外一场平局  
                            perfect = (s - t) * 3 + 1;  
                        else  
                            if (s >= n - 1) //如果能第一场输t分，然后其余n-1场每场都至少赢1球。  
                                perfect = 3 * (n - 1);  
                            else //如果不够的话n-1场里会有几场是平的。  
                                perfect = 3 * s + (n - 1) - s;  
                    //然后是最坏情况。  
                    //即第一场把s球全部踢进.然后看一下丢球数够不够填满n-1场。  
                    if (t >= n - 1)  
                        bad = 3;  
                    else //不够的话就是平局了。  
                        bad = 3 + (n - 1) - t;  
                }             
        printf("%I64d %I64d\n", perfect, bad);  
    }  
    return 0;  
}
