            scanf("%lld",&a[i]);
            q.push(a[i]);
        }
        for(register int i=1;i<=n;i++){
            tmp=q.top(),q.pop();
            if(a[i]!=tmp){
                ans++;
                for(register int j=i+1;j<=n;j++) if(a[j]==tmp) swap(a[i],a[j]);
            }
        }
        printf("%d",ans);
    }
