```cpp
#include<bits/stdc++.h>
#define ll long long
using namespace std;
ll C[500005],n,A,m,a,b;
string s;
ll lowbit(ll x){
	return x&(-x);
}
void qwe(ll i,ll A){
	for(int j=i;j<=n;j+=lowbit(j)){
		C[j]+=A;
	}
}
ll getsum(ll x){
	ll sum=0;
	for(int i=x;i>0;i-=lowbit(i)){
		sum+=C[i];
	}
	return sum;
}
int main(){
	ll ans=0;
	while(true){
		ans=0;
		scanf("%lld",&n);
		if(n==0)return 0;
		memset(C,0,sizeof(C));
		for(int i=1;i<=n;i++){
			scanf("%lld",&A);
			qwe(i,1);
			ans=ans+getsum(n)-getsum(A);
		}
		cout<<ans<<endl;
	}
	return 0;
}
```
