	for(int i=1;i<=n;i++)dis[e[i].id]=i;//给dis数组赋初值
	for(int i=1;i<=n;i++)while(dis[i]!=i)std::swap(dis[i],dis[dis[i]]),ans++;//一直讨论到dis回到初值
