```cpp
#include<bits/stdc++.h>
using namespace std;
int n,a,b;
int ansA=0,ansB=0;//两人总分数
int w[5][5]={{0,2,1,1,2},{1,0,2,1,2},{2,1,0,2,1},{2,2,1,0,1},{1,1,2,2,0}};
//打表大法：1win 2lose
int main()
{
	queue<int> q;//定义队列
	queue<int> p;
	cin>>n>>a>>b;
	for(int i=1;i<=a;i++)
	{
		int x;
		cin>>x;
		q.push(x);//将元素依次放入队列q
	}
	for(int i=1;i<=b;i++)
	{
		int x;
		cin>>x;
		p.push(x);//将元素依次放入队列p
	}
	for(int i=1;i<=n;i++)
	{
		int r1=q.front();
 		int r2=p.front();//取出他们两个下一个要出的
 		q.pop();
		p.pop();//用了就弹出
		q.push(r1);
		p.push(r2);//再放入队列末尾，以便下次使用
		if(w[r1][r2]==1)//判断谁赢
		{
		    ansA++;
		    continue;
		}	
		if(w[r1][r2]==2)
		{
			ansB++;
		}
	}
	cout<<ansA<<' '<<ansB;
	return 0;
}


```

