```cpp
#include<bits/stdc++.h>
using namespace std;
queue<int> q1;
queue<int> q2;
int n,n1,n2,a,b,i,j,l,sum1,sum2;
inline void Read()
{
	scanf("%d%d%d",&n,&n1,&n2);
	for(i=1;i<=n1;i++)
	{
		scanf("%d",&a);
		q1.push(a);
	}
	for(i=1;i<=n2;i++)
	{
		scanf("%d",&b);
		q2.push(b);
	}
}
inline void solve()
{
	for(i=1;i<=n;i++)
	{
		a=q1.front();
		b=q2.front();
		q1.pop();
		q2.pop();
		q1.push(a);
		q2.push(b);
		if(a==0)
		{
			if(b==0) continue;
			if(b==1) sum2++;
			if(b==2) sum1++;
			if(b==3) sum1++;
			if(b==4) sum2++;
		}
		if(a==1)
		{
			if(b==0) sum1++;
			if(b==1) continue;
			if(b==2) sum2++;
			if(b==3) sum1++;
			if(b==4) sum2++;
		}
		if(a==2)
		{
			if(b==0) sum2++;
			if(b==1) sum1++;
			if(b==2) continue;
			if(b==3) sum2++;
			if(b==4) sum1++;
		}
		if(a==3)
		{
			if(b==0) sum2++;
			if(b==1) sum2++;
			if(b==2) sum1++;
			if(b==3) continue;
			if(b==4) sum1++;
		}
		if(a==4)
		{
			if(b==0) sum1++;
			if(b==1) sum1++;
			if(b==2) sum2++;
			if(b==3) sum2++;
			if(b==4) continue;
		}
	}
	printf("%d %d",sum1,sum2);
}
int main()
{
	Read();
	solve();
	return 0;
}
```
