int y[200];
void next()
{
    if(xp==xn-1)
    {
        xp=0;
    }
    else
    {
        xp++;
    }
    if(yp==yn-1)
    {
        yp=0;
    }
    else
    {
        yp++;
    }
}
int main()
{
    int i;
    int xc,yc;
    int count;
    xp=0;
    yp=0;
