#include <bits/stdc++.h>
using namespace std;

int game[5][5] = {  {0, -1, 1, 1, -1},
                    {1, 0, -1, 1, -1},
                    {-1, 1, 0, -1, 1},
                    {-1, -1, 1, 0, 1},
                    {1, 1, -1, -1, 0}};
queue <int> que1, que2;

int main()
{
    int n, na, nb;
    scanf("%d%d%d", &n, &na, &nb);
    for (int i = 0; i < na; i++) {int tmp; scanf("%d", &tmp); que1.push(tmp);}
    for (int i = 0; i < nb; i++) {int tmp; scanf("%d", &tmp); que2.push(tmp);}
    int ans1 = 0, ans2 = 0;
    for (int i = 0; i < n; i++) {
        int x = que1.front(), y = que2.front();
        que1.pop(), que2.pop();
        switch(game[x][y]) {
        case 0: break;
        case 1: ans1++;break;
        case -1: ans2++;break;
        }
        que1.push(x), que2.push(y);
    }
    printf("%d %d\n", ans1, ans2);
    return 0;
}

