```cpp
#include <iostream>
#include <cstdio>
#include <cstring>
using namespace std;
int ll, rr, u, d, cnt, r, c;//u为上，d为下，ll为左，rr为右
char hai[1100][1100];//方向数组
int vis[1100][1100];
int dx[] = {1, -1, 0, 0};
int dy[] = {0, 0, 1, -1};
void dfs(int x, int y) {
	for (int i = 0; i <= 3; i++) {
		int nx = x + dx[i];
		int ny = y + dy[i];
		if (nx < 0 || nx >= r || ny < 0 || ny >= c) continue;
		if (hai[nx][ny] == '#' && !vis[nx][ny]) {
			vis[nx][ny] = 1;
			ll = min(ll, ny);
			rr = max(rr, ny);
			u = min(u, nx);
			d = max(d, nx);
			dfs(nx, ny);
		}
	}
}
int check() {
	for (int i = u; i <= d; i++) {
		for (int j = ll; j <= rr; j++) {
			if (!vis[i][j]) return 1;//若一个点未被访问过，即两船相邻
		}
	}
	return 0;
}//检查长方形内部的点是否被访问过
int main()
{
	scanf("%d%d", &r, &c);
	for (int i = 0; i < r; i++) {
		cin >> hai[i];
	}
	for (int i = 0; i < r; i++) {
		for (int j = 0; j < c; j++) {
			if (hai[i][j] == '#' && !vis[i][j]) {
				cnt++;
				vis[i][j] = 1;
				ll = rr = j;
				u = d = i;
				dfs(i, j);
				if (check()) {
					printf("Bad placement.");
					return 0;
				}
			}
		}
	}
	printf("There are %d ships.", cnt);
	return 0;
}
