#include <cstring>
using namespace std;
struct blood
{
    int x,y,z;
}hero[300001],t;//定义结构体（数组开大不是罪啊啊啊！！！）
queue <blood> q;//STL队列
int n,m,a,b,fx[]={0,1,0,-1},fy[]={1,0,-1,0},x,y,map[1001][1001],zx,zy;
int main()
{
    memset(map,-1,sizeof(map));
    cin>>n>>m>>a>>b;
    for (int i=1;i<=a;i++) 
    {
        cin>>zx>>zy;
        map[zx][zy]=0;
        t.x=zx;t.y=zy;
        q.push(t);//边读入边入队
    }
    for (int i=1;i<=b;i++) cin>>hero[i].x>>hero[i].y;
    while (!q.empty())
    {
        for (int j=0;j<4;j++)
        {
            t=q.front();//取首个
            x=t.x+fx[j];y=t.y+fy[j];//计算四个方向
            if (x<1||x>n||y<1||y>m||map[x][y]!=-1) continue;//如果越界或已走过，就不搜索
            t.x=x;t.y=y;t.z++;
            q.push(t); //将新结果入队
            map[x][y]=t.z;
        }
        q.pop();//队首出队
    }
    for (int i=1;i<=b;i++) cout<<map[hero[i].x][hero[i].y]<<endl;
    return 0; 
}
