using namespace std;
#pragma GCC optimize(2) //O2优化
#pragma GCC optimize(3) //吸臭氧
struct per
{
	int x,y,s;
//  x , y , step
	per(int xx,int yy,int ss)
	{x=xx;y=yy;s=ss;}
};
queue<per> q;
bool vis[505][505];
int f[505][505];
int sx,sy,m,n;
//BFS模板
bool in(int x,int y)
{
	return x<1 or y<1 or x>m or y>n or vis[x][y];
}
void bfs(void)
{
	int dir[4][2]={0,-1,-1,0,0,1,1,0};
	q.push(per(sx,sy,0));
	f[sx][sy]=0;
	vis[sx][sy]=1;
	while(!q.empty())
	{
		int x=q.front().x,y=q.front().y,s=q.front().s;
		for(int k=0;k<4;k++)
		{
			int tx=x+dir[k][0];
			int ty=y+dir[k][1];
			if(in(tx,ty)) continue;
			if(s+1>f[tx][ty]) continue;
            //判断是否是最短距离
            //如果是,继续搜索
            //如果不是,停止搜索该支点
			q.push(per(tx,ty,s+1));
			f[tx][ty]=s+1;
			vis[tx][ty]=1;
		}
		q.pop();
	}
	while(not q.empty()) q.pop();
    //清空,为了下一次的搜索
}
int input();
int main()
{
	int a,b;
	scanf("%d%d%d%d",&m,&n,&a,&b);
	memset(f,0x7f,sizeof(f));
	while(a--)
	sx=input(),sy=input(),vis[sx][sy]=1,bfs(),memset(vis,0,sizeof(vis));//a只用一次
	while(b--)
	printf("%d\n",f[input()][input()]);//b只用一次,直接出答案
	return 0;
}
//快读
int input()
{
	int re=0;char c=getchar();
	while(!isdigit(c)) c=getchar();
	while(isdigit(c)) re=(re<<3)+(re<<1)+(c^48),c=getchar();
	return re;
}
