    for (i=1;i<=n;i++)
    {
        cin>>t;
        s.insert(t);
    }
    while (s.size()>1)
    {
        sumof12=*s.begin();
        s.erase(s.begin());
        sumof12+=*s.begin();
        s.erase(s.begin());
        s.insert(sumof12);
        ans+=sumof12;
    }
    cout<<ans;
}
