        Matrix ret={},tmp=*this;
        ret.r=ret.c=r;
        for(int i=0;i<r;i++) ret.num[i][i]=1;
        while(k)
        {
            if(k&1) ret=ret*tmp;
            tmp=tmp*tmp;
            k>>=1;
