using namespace std;
const int mod=1e5+3;
int a,b,c,d,k;
int C[2010][2010];
int fac[1010];
int ans;
signed main()
{
	scanf("%lld%lld%lld%lld%lld",&a,&b,&c,&d,&k);
	for(int i=0;i<=2000;i++)
	{
		C[i][0]=C[i][i]=1;
	}
	for(int i=1;i<=2000;i++)
	{
		for(int j=1;j<=2000;j++)
		{
			C[i][j]=(C[i-1][j]+C[i-1][j-1])%mod;
		}
	}
	fac[0]=1;
	for(int i=1;i<=k;i++)
	{
		fac[i]=fac[i-1]*i%mod;
	}
	for(int i=0;i<=k;i++)
	{
		ans+=C[a][i]%mod*C[b+d-(k-i)][i]%mod*fac[i]%mod*C[c][k-i]%mod*C[d][k-i]%mod*fac[k-i]%mod;
		ans%=mod;
	}
	printf("%lld",ans);
	return 0;
}
