#include<iostream>
using namespace std;
int n,cnt;
struct nod{
	int to,nxt;
}e[5000001];

int head[6001];

int a[6001];

int f[6001];

int g[6001];

void addEdge(int u,int v){
	e[++cnt]=(nod){v,head[u]};
	head[u]=cnt;
}

void dfs(int s,int fa){
	if(g[s]!=-129) return;
	g[s]=a[s];
	for(int i=head[s];i;i=e[i].nxt){
		int v=e[i].to;
		if(v==fa) continue;
		dfs(v,s);
		f[s]=max(g[v]+f[s],f[s]);
		f[s]=max(g[v],f[s]);
		g[s]=max(g[s]+f[v],g[s]);
		g[s]=max(g[s],f[v]);
	}
}

int main(){
