```cpp
#include<bits/stdc++.h>
using namespace std;
int n,a[6005];
int ver[6005],Next[6005],head[6005],tot;//邻接表存边
int f[6005][2],x,y;
bool fa[6005];
void add(int x,int y)
{
	ver[++tot]=y;
	Next[tot]=head[x];
	head[x]=tot;
}
void dp(int x)//树形dp，上文解释了
{
	f[x][0]=0;
	f[x][1]=a[x];
	for(int i=head[x];i;i=Next[i])
	{
		int y=ver[i];
		dp(y);
		f[x][0]+=max(f[y][0],f[y][1]);
		f[x][1]+=f[y][0];
	}
}
int main()
{
	scanf("%d",&n);
