void make()//建多叉树
{
    int x,y,i;
    memset(c,1,sizeof(c));
    for (int i=1;i<=n-1;i++)
    {
        scanf("%d%d",&x,&y);
        b[y][++b[y][0]]=x;
        c[x]=false;
