          re=i; break;        //re：找出根节点
      }
}
void dfs(int i)//递归过程
{
    int k;
    if (b[i][0]==0)     //如果到了叶子节点，就直接用开心值代替
      {
          f[i][1]=a[i];
          return;
      }
    for (k=1;k<=b[i][0];k++)
      {
          dfs(b[i][k]);
          f[i][1]+=f[b[i][k]][0];
          f[i][0]+=max(f[b[i][k]][0],f[b[i][k]][1]);
      }
    f[i][1]+=a[i];
}
int main()
{
