using namespace std;
const int Max = 6005;
int a[Max];
bool use[Max];
vector<int>f[Max];
int fa[Max][2]; 

void  acioi(int x)
{
	fa[x][1] = a[x];
	fa[x][0] = 0;
	for(register int i = 0;i < f[x].size();i ++)
	//这里不能是i <= f[x].size() - 1因为.size返回的是一个指针不是一个数没有办法进行下去 
	{
		int y = f[x][i];
		acioi(y);
		fa[x][0] += max(fa[y][0],fa[y][1]);
		fa[x][1] += fa[y][0];
	}
}

