# 一道经典的树形DP


```
#include <ios>
#include <iostream>
#include <algorithm>
#pragma GCC optimize(2)  //没事瞎玩儿
using namespace std;
int head[20000], vet[20000], Next[20000];//边表
int dp[20000][2], p[10000], ind[10000];
//dp[x][0]表示x点不取，x及其子树所能达到的最大快乐指数
//dp[x][1]表示x点取，x及其子树所能达到的最大快乐指数
//ind[x]表示x的入度，用来查找根
int edgenum, n, u, v, root;
inline void addedge(int u, int v)
{
	edgenum++;
	vet[edgenum] = v;
	Next[edgenum] = head[u];
	head[u] = edgenum;
}
inline void dfs(int u)
{
	dp[u][1]+=p[u];//如果u点取，还要加上自己的快乐指数
	for (register int e = head[u]; e; e = Next[e])
	{
		int v = vet[e];
		dfs(v);//从叶子节点向上DP
		dp[u][0] += max(dp[v][0], dp[v][1]);
		//当u点不取，v点可取，可不取，取稍大进行累加
		dp[u][1] += dp[v][0];
		//当u点不取，v点只能不取，进行累加
	}
}
int main()
{
	ios::sync_with_stdio(false);
	cin >> n;
	for (register int i = 1; i <= n; i++)
		cin >> p[i];
	for (int i = 1; i < n; i++)
	{
		cin >> u >> v;
		addedge(v, u);//只能是上司连向下属，否则会死循环
		ind[u]++;
	}
	for (register int i = 1; i <= n; i++)
		if (ind[i] == 0)
		{
			root = i;
			break;
		}
	dfs(root);
	cout<<max(dp[root][0], dp[root][1])<<'\n';
	//答案是根取和不取的较大值
	system("pause");
	return 0;
}

