		tree[a].push_back(b);
		tree[b].push_back(a);
	}
	dfs(1,0);
	printf("%d",ans);
return 0;
}

void dfs(int root,int hisfather)
{
	dp[root]=num[root];
	for(int i=0;i<tree[root].size();i++)
	{
		int to=tree[root][i];
		if(to==hisfather)continue;
