```cpp
#include<bits/stdc++.h>
#define maxn 7000
using namespace std;
int gs[maxn],d[maxn],w[maxn],s[maxn];
vector<int> g[maxn];
int n;

int dp(int x,int fa)
{
    if(d[x]) return d[x];//如果搜过了就返回 
    for(int i=0;i<g[x].size();i++)
    {
        int m=g[x][i];
        if(m==fa) continue;//不能再搜索他的父节点 
        dp(m,x);
        s[x]+=d[m];
        if(fa!=-1) gs[fa]+=d[m];
    }
    return d[x]=max(w[x]+gs[x],s[x]);
}

int main()
{
    cin>>n;
    for(int i=1;i<=n;i++)
        cin>>w[i];
    for(int i=1;i<n;i++)
    {
        int t1,t2;
        cin>>t1>>t2;
        g[t1].push_back(t2);
        g[t2].push_back(t1);
    }
    cout<<dp(1,-1)<<endl;
    return 0;
} 
