    while(scanf("%d%d",&s,&f)!=EOF && s && f)
        add(f,s),add(s,f),fa[s] = f;
    find_root();
    dfs(root);
    int ans = max(dp[root][0],dp[root][1]);
    printf("%d\n",ans);
}

