int n,i,j,k,m;
int son[6001][1001],f[6001][2],fa[6001];  //f[i][0]表示不选i时的状态，f[i][1]表示选i时的状态
int a[6001],maxx=0;
int doit(int t)  //dp主体
{
         int i,j,k;
         if (son[t][0]==0)  //如果当前结点没有儿子，就进行边界状态的处理
    {
            f[t][0]=0;
            f[t][1]=a[t];
            return 0;
    }
       f[t][1]=a[t];  //先加上当前节点的值
     for (i=1;i<=son[t][0];i++)
    {
             doit(son[t][i]);  //把子节点都dp一遍
             f[t][1]+=f[son[t][i]][0];  //如果选当前节点，就加上他儿子不选时的值
             f[t][0]+=max(f[son[t][i]][1],f[son[t][i]][0]);  //如果不选，就分两种情况，他每一个的儿子可以选也可以不选，选一个大的累加
    }
}
int main()
{
       int u,v;
       scanf("%d",&n);
       memset(f,0,sizeof(f));
       memset(fa,0,sizeof(fa));
       memset(son,0,sizeof(son));
       for (i=1;i<=n;i++)  
       {
            scanf("%d",&a[i]);
    }
        scanf("%d%d",&u,&v);
        while (u&&v)
        {
                son[v][0]++;   //既存儿子也存爸爸
                son[v][son[v][0]]=u;
                fa[u]=v;
                scanf("%d%d",&u,&v);
       }
      for (i=1;i<=n;i++)
      {
               if (fa[i]==0)  //找根
          {
                  doit(i);   //从根开始做dp
              break;
           }
       }
       for (i=1;i<=n;i++)  //查找最大值
       {
                maxx=max(maxx,f[i][0]);
                maxx=max(maxx,f[i][1]);
        }
        printf("%d",maxx);
        return 0;
}
