	getchar();getchar();
	scanf("%*c%d%*c%d%*c",&C.x,&C.y);
	getchar();getchar();
	scanf("%*c%d%*c%d%*c",&P.x,&P.y);
	if((B-A)*(C-A)<0)	//重构三角形的顶点，让三角形当A在上时，B左C右 
		swap(A,B);
	if(On_Point())
		puts("4");
	else if(On_Edge())
		puts("3");
	else if(!inside())
		puts("2");
	else
		puts("1");
	return 0;
}
