    int kl=zt>>1;
    s.a[zt][kl]=1;
    if(num==k&&!(zt&1))
        return;
    s.a[zt][kl+(1<<(m-1))]=1;
}
inline void dfs(register int x,register int num,register int zt)
{
    if(x==m+1)
