inline int A(int n,int m)
{
	int sum=1;
	for(register int i=1; i<=n; i++)
		sum=(sum*(m-i+1))%10007;
	return sum;
}
inline int c(int n,int m)
{
	int sum=1;
	for(register int i=1; i<=n; i++)
		sum=(sum*(m-i+1))%10007;
	for(register int i=1;i<=n;i++)
	{
		while(sum%i!=0)sum+=10007;
		sum/=i;
	 } 
	return sum;
}
int n,m,a[10010],sum=0;
int main()
{
	m=read();
	n=read();
	for(register int i=1; i<=n; i++)
	{
		a[i]=read();
		sum+=a[i];
