	if(sum>m)
	{
		printf("0\n");
		return 0;
	}
	sum=1;
	for(register int i=1; i<=n; i++)
	{
		sum=(sum*c(a[i],m))%10007;
		m-=a[i];
	}
	printf("%d\n",sum);
	return 0;
