    ans = (mul[n] * swift(mul[sum] * mul[n - sum], p - 2)) % p;//处理出上文所述的第一步
    for(int i = 1; i <= m; i++) {
        ans = (ans * ((mul[sum] * swift(mul[a[i]] * mul[sum - a[i]], p - 2)) % p)) % p;
