	for(i=1;i<n;i++)
	for(j=i+1;j<=n;j++)
	{
		cin>>map[i][j];
	}
	for(i=1;i<=n;i++)
	for(j=i+1;j<=n;j++)
	{
		for(k=i+1;k<j;k++)//枚举每一个中转站x
		map[i][j]=min(map[i][j],map[i][k]+map[k][j]);
	}
	cout<<map[1][n];
	return 0;
}
```

