```cpp
#include<cstdio>
#include<vector>
#include<queue>
using namespace std;
struct Chark{
    vector<int> link[10000005];
    vector<int> val[10000005];
}s;
int spfa(Chark a,int len,int st,int ed)//此处可作直接复制粘贴使用的模块
{
    queue<int> que;
    bool vis[10005];
    int d[10005];
    for(int i=1;i<=len;i++) d[i]=9999999;
    for(int i=1;i<=len;i++) vis[i]=false;
    d[st]=0;
    que.push(st);
    vis[st]=true;
    while(!que.empty())
    {
        int u=que.front();
        que.pop();
        vis[u]=false;
        for(int i=0;i<a.link[u].size();i++)
        {
            int w=a.link[u][i];
            if(d[w]>d[u]+a.val[u][i])
            {
                d[w]=d[u]+a.val[u][i];
                if(!vis[w])
                {
                    vis[w]=true;
                    que.push(w);
                }
            }
        }
    }
    return d[ed];
}
int main()
{
    int n;
    scanf("%cd",&n);
    for(int i=1;i<=n-1;i++)
     for(int j=i+1;j<=n;j++)
     {
         int c;
         scanf("%d",&c);
         s.link[i].push_back(j);
         s.val[i].push_back(c);
     }
    printf("%cd",spfa(s,n,1,n));
    return 0;
}
