#include<cstdio>
#include<cstring>
using namespace std;
int n,z,op,head[50009],next[50009],dis[50009],len[50009],v[50009],l,r,team[50009],pd[50009],u,v1,e,out,fh;
char cc;
int read()//读入优化
{
    out=0,fh=1,cc=getchar();
    if(cc=='-'){fh=-1;}
    while(cc>'9'||cc<'0'){cc=getchar();}
    while(cc>='0'&&cc<='9')
    {
        out=out*10+cc-'0',
        cc=getchar();
    }
    return out*fh;
}
void lt(int x,int y,int z)//单向联通
{
    op++,v[op]=y;
    next[op]=head[x],head[x]=op,len[op]=z;
    return;
}
void SPFA(int s)//SPFA……
{
    for(int i=1;i<=50000;i++){dis[i]=23333333;}
    memset(team,0,sizeof(team));
    memset(pd,0,sizeof(pd));
    l=0,r=1,team[1]=s,pd[s]=1,dis[s]=0;
    while(l!=r)
    {
        l=(l+1)%50000,u=team[l],pd[u]=0,e=head[u];
        while(e!=0)
        {
            v1=v[e];
            if(dis[v1]>dis[u]+len[e])
            {
                dis[v1]=dis[u]+len[e];
                if(!pd[v1])
                {
                    r=(r+1)%50000,
                    team[r]=v1,
                    pd[v1]=1;
                }
            }
            e=next[e];
        } 
    }
    return;
}
int main()
{
    n=read();
    for(int i=1;i<=n-1;i++)
    {
        for(int j=i+1;j<=n;j++)
        {
            z=read();//建路
            lt(i,j,z);
        }
    }
    SPFA(1);//从1开始跑SPFA
    printf("%d",dis[n]);//输出
    return 0;
}
