inline void dfs(int x,int y) {
	if(flag) return;
	if(x>=n || y>=m) {
		if(f[x%n][y%m]==2||f[x%n+n][y%m]==2||f[x%n][y%m+m]==2||f[x%n+n][y%m+m]==2) { //边界
			flag=true;
			return;
		}
	}
        //这个位置走过了
	f[x][y]=2;
        //判断是不是能走
	if(!f[(x+1+2*n)%(2*n)][y]) dfs((x+1+2*n)%(2*n),y);
	if(!f[(x-1+2*n)%(2*n)][y]) dfs((x-1+2*n)%(2*n),y);
	if(!f[x][(y+1+2*m)%(2*m)]) dfs(x,(y+1+2*m)%(2*m));
	if(!f[x][(y-1+2*m)%(2*m)]) dfs(x,(y-1+2*m)%(2*m));
}
