	if(now=='-')minus=!minus;
	for(;isdigit(now);now=getchar())i=(i<<3)+(i<<1)+(now^'0');
	return minus?-i:i;
}inline void output(long long o){
	if(o<0)putchar('-'),o=-o;
	if(o>=10)output(o/10);
	putchar(o%10^'0');
}template<typename name>name* expression(name* l,name* r){
	int n=r-l,i=0,j=1;
	for(int k=0,I,J,bound;i<=n&&j<=n&&k<=n;)
	{
		I=(i+k-1)%n+1,J=(j+k-1)%n+1;
		if(l[I]==l[J])k++;
		else
		{
			if(l[I]<l[J])swap(i,j);
			bound=max(i+k,j-1);
			if(i<j)j=bound+2;
			i=bound+1,k=0;
		}
	}
	return l+min(i,j);
}
