	for(rint i = 0;i <= 4;i++)//枚举4种转移+一种待在原地 
	{	
		rint tar_x = x + opt[i].xx;
		rint tar_y = y + opt[i].yy;

		if(map[tar_x][tar_y]) continue;// 撞墙走不通 

		if(tar_x > n || tar_x < 1 || tar_y > m || tar_y < 1) continue;//超出边界 

		dfs(tar_x,tar_y,time,depth + 1,max_depth);//搜索下一层 
	}
}

