#include<cstdio>
#include<algorithm>
#include<cstring>
#define N 15

using namespace std;

int n,m,ans;
int step[N][N];
bool vis[N][N];

inline void clear(){
	memset(vis,0,sizeof(vis));
	memset(step,0,sizeof(step));
}

void dfs(int x,int y,int cur){
	ans=max(ans,cur);
	if(cur+1<step[x][y]) return;
	step[x][y]=cur; 
	// 文中提到的玄学优化 
	for(int k=1;k<=4;k++){
		if(k==1){
			if(x+1>n || x+2>n || y-1<1 || y+1>m) continue;
			if(!vis[x+1][y] && !vis[x+2][y] && !vis[x][y-1] && !vis[x][y+1]){
				vis[x+1][y]=vis[x+2][y]=vis[x][y-1]=vis[x][y+1]=1;
				for(int i=1;i<=n;i++)
					for(int j=1;j<=m;j++)
						if(!vis[i][j])
							dfs(i,j,cur+1);
				vis[x+1][y]=vis[x+2][y]=vis[x][y-1]=vis[x][y+1]=0;
			}
		}
		else if(k==2){
			if(x-1<1 || x-2<1 || y-1<1 || y+1>m) continue;
			if(!vis[x-1][y] && !vis[x-2][y] && !vis[x][y-1] && !vis[x][y+1]){
				vis[x-1][y]=vis[x-2][y]=vis[x][y-1]=vis[x][y+1]=1;
				for(int i=1;i<=n;i++)
					for(int j=1;j<=m;j++)
						if(!vis[i][j])
							dfs(i,j,cur+1);
				vis[x-1][y]=vis[x-2][y]=vis[x][y-1]=vis[x][y+1]=0;
			}
		}
		else if(k==3){
			if(x-1<1 || x+1>n || y+1>m || y+2>m) continue;
			if(!vis[x+1][y] && !vis[x-1][y] && !vis[x][y+1] && !vis[x][y+2]){
				vis[x+1][y]=vis[x-1][y]=vis[x][y+1]=vis[x][y+2]=1;
				for(int i=1;i<=n;i++)
					for(int j=1;j<=m;j++)
						if(!vis[i][j])
							dfs(i,j,cur+1);
				vis[x+1][y]=vis[x-1][y]=vis[x][y+1]=vis[x][y+2]=0;
			}
		}
		else if(k==4){
			if(x-1<1 || x+1>n || y-1<1 || y-2<1) continue;
			if(!vis[x+1][y] && !vis[x-1][y] && !vis[x][y-1] && !vis[x][y-2]){
				vis[x+1][y]=vis[x-1][y]=vis[x][y-1]=vis[x][y-2]=1;
				for(int i=1;i<=n;i++)
					for(int j=1;j<=m;j++)
						if(!vis[i][j])
							dfs(i,j,cur+1);
				vis[x+1][y]=vis[x-1][y]=vis[x][y-1]=vis[x][y-2]=0;
			}
		}
		// 尝试放置各种形状 
	}
}

int main(){
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++)
		for(int j=1;j<=m;j++){
			clear(); // 清空，防止影响以后判断 
			dfs(i,j,0);
		}
	printf("%d\n",ans);
	return 0;
} 
