### 上代码(~~感觉已经讲的够清楚了,~~代码里就不多加注释了)：
~~~cpp
#include<cstdio>
#include<vector>
#include<cstring>
#include<algorithm>
using namespace std;
int jc[14]={1,1,2,6,24,120,720,5040,40320,362880,3628800,39916800,479001600,6227020800};
int dp[11],a[11],n,k,x;
inline int dfs(int pos,int lim,int zero)
{
	if(pos==-1)
		return 1;
	if(!lim&&!zero&&dp[pos]!=-1)
		return dp[pos];
	int up=lim?a[pos]:9,num=0;
	for(int i=0;i<=up;i++)
	{
		if(i!=4&&i!=7&&!(zero&&i==0))
			continue;
		num+=dfs(pos-1,lim&&i==up,zero&&i==0);
	}
	if(!zero&&!lim)
		dp[pos]=num;
	return num;
}
bool ok(int x)
{
	bool flag=1;
	while(x)
	{
		flag&=((x%10==4)||(x%10==7));
		x/=10;
	}
	return flag;
}
int solve1(int x)
{
	int k=0;
	while(x)
	{
		a[k++]=x%10;
		x/=10;
	}
	return dfs(k-1,1,1);
}
int solve2(int n,int x,int k)
{
	vector<int>v,a;
	for(int i=n-x+1;i<=n;i++)
		v.push_back(i);
	for(int i=x;i>=1;i--)
	{
		int r=k%jc[i-1];
		int t=k/jc[i-1];
		k=r;
		sort(v.begin(),v.end());
		a.push_back(v[t]);
		v.erase(v.begin()+t);
	}
	int j=n-x+1,res=0;
	for(int i:a)
	{
		if(ok(j)&&ok(i))
			res++;
		j++;
	}
	return res;
}
int main()
{
	scanf("%d%d",&n,&k);
	if(n<=12&&1LL*k>jc[n])
		return 0*printf("-1\n");
	memset(dp,-1,sizeof(dp));
	while(k>jc[x])
		x++;
	int ans=solve1(n-x)-1;
	ans+=solve2(n,x,k-1);
	printf("%d",ans);
	return 0;
}

