#include<algorithm>
using namespace std;
long long n,k,tot,tot2,temp=20,temp2,cnt,ans;
long long a[2100000],b[210],c[210],jc[210],re[210];
void dfs(long long now,long long ans)
{//寻找所有lucky num
	ans+=4*now;
	if(ans>n)
		return;
	a[++tot]=ans;
	dfs(now*10,ans);
	ans+=3*now;
	if(ans>n)
		return;
	a[++tot]=ans;
	dfs(now*10,ans);
}
bool isluck(int x)//用来判断x是不是lucky num
{
	for(int i=tot;i>=1;i--)
	if(a[i]==x)
		return true;
	else if(a[i]<x)
		break;
	return false;
}
void work()
{//主要过程
	long long temp3=k;
	while(temp>0)//确定从后向前第temp位的数字
	{
		int sxz=temp3/jc[temp]+1;
