void make_sequence(long long a,long long b){
	b--;
	int p=a;
	if(p>14)p=14;
	for(int i=p;i>=1;i--){
		c[p-i+1]=b/jc[i-1]+1;
		int cnt=0,r=c[p-i+1];
		for(int j=1;j<=c[p-i+1];j++){
			if(!vis[j])cnt++;
			if(j==c[p-i+1]&&cnt<r)c[p-i+1]++;
		}
		vis[c[p-i+1]]=1;
		b%=jc[i-1];
	}
}
