	if(y>n)
		return ;
	a[++tot]=y;
	dfs(x*10,y);
}
bool pd(long long x)
{
	long long i;
	for(i=tot;i>=1;i--)
	{
		if(x==a[i])
			return true;
		else if(x>a[i])//可有可无，减枝优化而已
			break;
	}
	return false;
}

void CodeForces()
{
	long long temp3=k;
	while(temp)
	{
		int xxx=temp3/fact[temp]+1;
		AC[++rap]=c[xxx];
		for(int i=xxx;i<=temp;i++)
		  c[i]=c[i+1];
		temp3%=fact[temp];
		temp--;
	}
	AC[++rap]=c[1];
	for(int i=1;i<=tot2;i++)
	{
		if(pd(AC[b[i]-temp2+1]))
	    s++;
	}
}
