		return 0;
	}
	sort(a+1,a+tot+1);排序一下，方便后续的处理
	while(temp>0)
   {
		if(fact[temp]<=k)
			break;
		temp--;
	}
	temp2=n-temp;
	for(int i=tot;a[i]>=temp2;i--) 
	  b[++tot2]=a[i];
	for(int i=temp2;i<=n;i++)
	  c[i-temp2+1]=i;
	s=tot-tot2; 
