    for(int j=1;j<=m;j++)
    if(b[i][j])
    f[i][j]=min(min(s[i][j],w[i][j]),f[i-1][j-1]+1);//状态转移方程 ; f i , j 表示以点 i , j 为正方形的右下角得到的最大边长
    for(int i=1;i<=n;i++)
    for(int j=1;j<=m;j++)
    ans=max(ans,f[i][j]);//比较每一个点
    printf("%d",ans);
    return 0;
}
