```cpp
#include<cstdio>
#include<cstdlib>
#include<algorithm>
using namespace std;
int a[101][101],m,n,ans=0;
int findsquare(int x,int y)//找出以(x,y)为坐上角最大的正方形
{
    int lp;
    for(lp=1;lp<=min(n,m);lp++)
    {
        for(int tx=x;tx<x+lp;tx++)
        {
            for(int ty=y;ty<y+lp;ty++)
            {
                if(a[tx][ty]!=1)//如果已经出现0，就直接退出，不必继续搜索下去。
                {
                    return lp-1;因为当前的答案已经加1，所以这里要减去。
                }
            }
        }
    }
    return lp-1;//同理
}
void solve()
{
    scanf("%d %d",&n,&m);
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<n;j++)
        {
            scanf("%d",&a[i][j]);
        }
    }
    for(int i=0;i<n;i++)// 简单暴搜,一个一个找
    {
        for(int j=0;j<n;j++)
        {
            int tmp;
            tmp=findsquare(i,j);
            if(tmp>ans)//求最大的。
            {
                ans=tmp;
            }
        }
    }
    printf("%d",ans);
}
int main()
{
    solve();
    return 0;
}
```

