看题解里貌似没有二分，于是打了一个二分查找的代码，还挺快的，20ms。用二分枚举正方形边长，再进行判断
```cpp
#include<bits/stdc++.h>
using namespace std;
int n,m,lu,ru,mid,searchh=1;
int areas[100][100];//存图
int checked(int p,int q,int leng){
    for(int x=p;x<leng+p;x++){
		for(int y=q;y<leng+q;y++){
			if(areas[x][y]==0){
				searchh=0;
				return 0;
			}
		}
	}
}//检索正方形里是否有0

int hehe(){
	for(int i=0;i+mid<n;i++){
		for(int j=0;j+mid<m;j++){
			if(areas[i][j]==1){
				searchh=1;
				checked(i,j,mid);
				if(searchh==1)return 1;
			}
		}
	}
	return 0;	
}//无视此处函数名。。。此函数用于枚举所有边长为mid的正方形

int main(){
	cin>>n>>m;
	bool check=0;
	for(int i=0;i<n;i++){
		for(int j=0;j<m;j++){
			cin>>areas[i][j];
			if(check==0&&areas[i][j]==1){
				check=1;
			}
		}
	}
	if(check==0){
		cout<<"0";
		return 0;
	}//全部是0的特判，~~貌似没啥用~~


	lu=1;ru=min(n,m);
	while(lu<ru-1){
		mid=(lu+ru)/2;
		if(hehe())lu=mid;
		else ru=mid;
	}
	cout<<lu;
	return 0;
}
