## Code
```cpp
#include "bits/stdc++.h"
#define cal(x,y,t) (t==1?x+y:x*y)//根据xy之间的符号进行计算
#define ll long long
using namespace std;

ll ans=-1,f[16][16];//f[i][j]表示第i个数到第j个数的最大值
int n,k,s[15],a[16];
//s[i]标记符号,若为1,表示a[i]和a[i+1]之间为加号,为2则是乘号

ll dp()//确定所有符号后dp求最值
{
	for(int i=1;i<=n;i++)
		f[i][i]=a[i];//初始化
	int j;
    //区间dp模板
	for(int l=1;l<=n;l++)//长度
		for(int i=1;i+l-1<=n;i++)//左端点
		{
			j=i+l-1;//右端点
			for(int k=i;k<j;k++)
				f[i][j]=max(f[i][j],cal(f[i][k],f[k+1][j],s[k]));
                //取原数或合并后的数的较大值
		}
	return f[1][n];//f[1][n]即为所求的一个解
}

int dfs(int x,int t1,int t2)//深搜确定乘号位置
{
	if(x==n)
	{
		memset(f,0,sizeof(f));//多次判断,记得清空数组
		ans=max(ans,dp());
		return 0;
	}
    //其实一个参数就够了,笔者这里还可以优化,不过这样方便理解
	if(t1<k)//枚举乘号
	{
		s[x]=2;
		dfs(x+1,t1+1,t2);
	}
	if(t2<n-k-1)//枚举加号
	{
		s[x]=1;
		dfs(x+1,t1,t2+1);
	}
}

int main()
{
	scanf("%d%d",&n,&k);
	for(int i=1;i<=n;i++)
		scanf("%d",&a[i]);
	dfs(1,0,0);//深搜确乘号位置
	printf("%lld",ans);
	return 0;
}
```

