#include<math.h>
using namespace std;

const int N=5e6+5;
int n,used[N];
long long f[N],mju[N],h[N];

void prework()
{
    int i,j;
    mju[1]=f[1]=1;
    for(i=2;i<=n;++i)
        if(!used[i])
        {
            mju[i]=-1;
            f[i]=i;
            for(j=i+i;j<=n;j+=i)
            {
                used[j]=1,f[j]=j;
                if((j/i)%i) mju[j]=-mju[j/i];
                else mju[j]=0;
            }
        }
//	for(i=1;i<=n;++i) cout<<mju[i]<<" "<<f[i]<<endl;//cout<<endl;
}

void Dirichlet(long long *f,long long *g,long long *h)
{
    int i,j;
    for(i=1;i<=n;++i)
        for(j=i;j<=n;j+=i)
            h[j]+=1LL*f[i]*g[j/i];
}

void work()
{
    int d;
    long long ans=0;
    for(d=1;d<=n;++d) ans+=1LL*(n/d)*(n/d)*h[d];
    long long calc=1LL*n*(n+1)/2;
    printf("%lld\n",(ans-calc)/2);
}

int main()
{
    scanf("%d",&n);
    prework();Dirichlet(f,mju,h);work();
    return 0;
}

